const pptxgen = require('pptxgenjs');
const html2pptx = require('./.claude/skills/pptx/scripts/html2pptx');

async function createPresentation() {
    const pptx = new pptxgen();
    pptx.layout = 'LAYOUT_16x9';
    pptx.author = 'Claude';
    pptx.title = 'Introduction to Quantum Physics';

    // Slide 1: Title
    await html2pptx('slide1.html', pptx);

    // Slide 2: What is Quantum Physics?
    await html2pptx('slide2.html', pptx);

    // Slide 3: Fundamental Principles
    await html2pptx('slide3.html', pptx);

    // Save
    await pptx.writeFile({ fileName: 'sample.pptx' });
    console.log('Presentation created successfully at sample.pptx');
}

createPresentation().catch(console.error);

